shader.vertex:
#version 330
layout(location = 0) in vec3 vertexPosition;

out vec3 pos;

uniform mat4 MVP;

void main(){
	gl_Position = MVP * vec4(vertexPosition,1);
	pos = vertexPosition;
}

shader.fragment:
#version 330
in vec3 pos;

//G Buffers
layout (location = 0) out vec4 gAlbedoSpec;

void main() {
	gAlbedoSpec = vec4(pos, 1.0);
}
